sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	var cdt = g_form.getValue('rc_entry'); //First Date/Time field
	var dttype = 'day'; //this can be day, hour, minute, second. By default it will return seconds.

	var ajax = new GlideAjax('global.ClientDateTimeUtils');
	ajax.addParam('sysparm_name','getNowDateTimeDiff');
	ajax.addParam('sysparm_fdt', cdt);
	ajax.addParam('sysparm_difftype', dttype);
	ajax.getXML(doSomething);

	function doSomething(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		if(answer >= 0){
			return true;
		} else {
			alert('Estimated RC Entry date cannot be in the past');
			g_form.setValue('rc_entry','');
		}


	}
}
