sys_ui_action.script:
current.status = 11;
current.active = 'false';
current.update();



var gr = new GlideRecord('x_prole_software_c_software_change_request_table');

gr.initialize();
gr.change_type = current.change_type;
gr.category = current.category;
gr.cmdb_ci = current.cmdb_ci;
gr.short_description = 'Copy of : ' + current.number + " - " + current.short_description;
gr.release_notes = current.release_notes;
gr.deployment_notes = current.deployment_notes;
gr.development_team = current.development_team;
gr.qa_lead = current.qa_lead;
gr.project_sponsor = current.project_sponsor;
gr.deadline = current.deadline;
gr.financial_impact = current.financial_impact;
gr.new_software = current.new_software;
gr.core_change = current.core_change;
gr.database_changes = current.database_changes;
gr.jira_stories = current.jira_stories;
gr.deadline_date = current.deadline_date;
gr.database_engineer = current.database_engineer;
gr.insert();

current.status = 11;
current.active = 'false';

gs.addInfoMessage("CCSX ticket " + gr.number + " created");
action.setRedirectURL(gr);
action.setReturnURL(current);
